package 클래스;

public class 초중급 {
	//배열을 선언하고 100이 넘는 개수가 몇개인지 찾으시오.
//	변수는 아래와 같이 선언.
//	개수가 3개 이하면 count에 5를 대입하시오.

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 0;
		int array[] = {101, 110, 120, 130, 88, 30};
		for(int i=0; i<array.length; i++) {
			if(array[i]>100) {
				count++;
				}
			}if(count<=3) {
				count=5;
			}else {
				System.out.println(count);
		}
			
			
//			6번
//			잔고에 20만원이 넘으면 money에 0을 대입하시오.
//			철수의 계좌잔고는 현재 157000이 있다.  
//			은행가서 5만원, 만원, 5천원 으로 교환을 하고 싶다.  
//			157000은 5만원 3장, 만원 0장, 5천원 1장으로 교환이 가능할 것 이다.  
			/*오만원 3장
			만원 0장
			오천원 1장*/
			 int money = 157000;
			 final int OMAN = 50000;
			 final int MAN = 10000;
			 final int OCHEON = 5000;
			 
			 int omanj = money/OMAN;
			 int omanjt = money%OMAN; //7000
			 
			 int mj = omanjt/MAN;
			 int mjt = omanjt%MAN;
			 
			 int oj = mjt/OCHEON; //1
			 if(money>200000) {
				 money=0;
			 }
			 System.out.println("오만원"+omanj+" 만원"+mj+" 오천원"+oj);
			 
	}
}
